import React, { Component } from 'react';
import '../css/Login.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import md5 from 'md5';
import Cookies from 'universal-cookie';

const baseUrl="http://localhost:3001/usuario";
const cookies = new Cookies();

class Login extends Component {
    state={
        form:{
            username: '',
            password: ''
        }
    }

    handleChange=async e=>{
        await this.setState({
            form:{
                ...this.state.form,
                [e.target.name]: e.target.value
            }
        });
    }

    iniciarSesion=async()=>{
        await axios.get(baseUrl, {params: {username: this.state.form.username, password: md5(this.state.form.password)}})
        .then(response=>{
            return response.data;
        })
        .then(response=>{
            if(response.length>0){
                var respuesta=response[0];
                cookies.set('id', respuesta.id, {path: "/"});
                cookies.set('apellido_paterno', respuesta.apellido_paterno, {path: "/"});
                cookies.set('apellido_materno', respuesta.apellido_materno, {path: "/"});
                cookies.set('nombre', respuesta.nombre, {path: "/"});
                cookies.set('username', respuesta.username, {path: "/"});
                alert(`Bienvenido ${respuesta.nombre} ${respuesta.apellido_paterno}`);
                window.location.href="./menu";
            }else{
                alert('El usuario o la contrase単a no son correctos');
            }
        })
        .catch(error=>{
            console.log(error);
        })

    }

    componentDidMount() {
        if(cookies.get('username')){
            window.location.href="./menu";
        }
    }


    render() {
        return (
    <div className="containerPrincipal">
        <div className="containerSecundario">
          <div className="form-group">
            <img className='encap' alt=''></img>
            <h1 className='title'>ENCAP | Escuela Nacional</h1>
            <label className='label'>Usuario: </label>
            <br />
            <input 
              type="text"
              className="form-control"
              placeholder='Nro. de documento'
              name="username"
              onChange={this.handleChange}
            />
            <br />
            <label className='label'>Contrase単a: </label>
            <br />
            <input
              type="password"
              placeholder='Ingrese su contrase単a'
              className="form-control"
              name="password"
              onChange={this.handleChange}
            />
            <br />
            <button className="btn-primary" onClick={()=> this.iniciarSesion()}>Continuar</button>
            <br/>
            <a href='https://www.jotform.com/es/form-templates/cc-21-movil'>Recupere o crea tu clave internet</a>

          </div>
        </div>
      </div>
        );
    }
}

export default Login;
